<?php $options = get_option('mikex_opts');global $wpdb;$results = $wpdb->get_results( 'SELECT * FROM `wp_term_taxonomy` WHERE taxonomy = "shop_vendor"');$t_id = array();foreach($results as $terms){		$t_id[] = $terms->term_id;}$n_all = array();foreach($t_id as $term){$name_all = $wpdb->get_results( 'SELECT name FROM `wp_terms` WHERE term_id = "'.$term.'"');$n_all[] = $name_all[0]->name;}$c = array_combine($t_id, $n_all);?><b>Vendore Header Image Uploader</b><br /><br /><form action="<?php echo plugins_url('update.php', __FILE__) ?>" method="POST" enctype="multipart/form-data"><?php _e('users:'); ?><select name="vendore"><option value="">Select</option><?phpforeach($c as $key => $value):echo '<option value="'.$key.'">'.$value.'</option>'; //close your tags!!endforeach;?></select><br><label for="file">Background Image:</label><input type="file" name="file" id="file"><br><label for="file">Profile Image:</label><input type="file" name="profileimage" id="profileimage"><br><label for="file">Personal Name:</label><input type="text" name="personalname" id="personalname"><br><input type="submit" name="submit" value="Submit"></form>