<?php/* Functions used by mikex.php */function mikex_activated() {	$mikex_version = 1.0;   /* These options are stored in the database. */	add_option("mikex_version", $mikex_version);		$default_opts = array('search' => 'searchtext',			      'replace' => 'replacetext',			      'replace-color' => 'replacecolortext',			      'toggle' => 'togglecoor'			      );	add_option("mikex_opts", $default_opts);}function mikex_deactivated() {/* We don't have anything to perform but you would put your deactivation   functions here. */   }/* add_menu_page creates a top level menu button. If you are looking for a   menu button to place inside Settings use add_options_page */    function mikex_add_menu_page() {    add_menu_page("Vendore Header Image Uploader", "Vendore Header Image Uploader", "activate_plugins", "MSS", "mikex_admin_page");}function mikex_admin_page() {    include_once dirname(__FILE__) . '/admin.php';}/* Functions used by admin.php */function mikex_admin_scripts() {        /* Register and queue the Stylesheet */    wp_register_style('mikex_admin_css', plugins_url('admin.css', __FILE__));    wp_enqueue_style('mikex_admin_css');        /* Register and queue the Javascript */    wp_register_script('mikex_admin_js', plugins_url('admin.js', __FILE__));    wp_enqueue_script('mikex_admin_js');    /* Load the jQuery that is already included in WordPress */    wp_enqueue_script('jquery');}   function mikex_client_scripts() {    /* Register and queue the Stylesheet */    wp_register_style('mikex_css', plugins_url('mikex.css', __FILE__));    wp_enqueue_style('mikex_css');        /* Register and queue the Javascript */    wp_register_script('mikex_js', plugins_url('mikex.js', __FILE__));    wp_enqueue_script('mikex_js');        /* Load the jQuery that is already included in WordPress */    wp_enqueue_script('jquery');}/* Functions used for posts */function mikex_replace_keyword() {        /* Retrieve our settings */    $options = get_option('mikex_opts');        /* If we want to create more complex html code it's easier to capture the output buffer and return it */    ob_start(); ?>        <span class="mikex-span" newColor="<?php echo $options['toggle'] ?>" style="color: <?php echo $options['replace-color'] ?>;"><?php echo stripslashes($options['replace']) ?></span><?php    /* Return the buffer contents into a variable */    $new_content = ob_get_contents();        /* Empty the buffer without displaying it. We don't want the previous html shown */    ob_end_clean();        /* The text returned will replace our shortcode matching text */    return $new_content; }?>